import React, { useEffect, useState } from 'react'
import ImageGallery from 'react-image-gallery';

function ProductImage(props) {
    const [Images, setImages] = useState([])

    useEffect(() => {
        if (props.detail.assets && props.detail.assets.length > 0) {
            let images = [];

            props.detail.assets && props.detail.assets.map(item => {
                images.push({
                    original: `http://localhost:5000/${item.src}`,
                    thumbnail: `http://localhost:5000/${item.src}`
                })
            })
            setImages(images)
        }
    }, [props.detail])

    return (
        < div >
            <ImageGallery items={Images} />
        </ div>
    )
}

export default ProductImage
