import React from 'react';
import { TextField, Grid } from '@material-ui/core';
import { useFormContext, Controller } from 'react-hook-form';

const FormInput = ({ name, label }) => {

    const { control } = useFormContext();
    const isError = false;
    /*
        return (
            <Grid item xs={12} sm={6}>
                <Controller
                    render={({ field }) => (
                        <TextField
                            fullWidth
                            name={name}
                            label={label}
                            required
                        />
                    )}
                    as={TextField}
                    control={control}
                    fullWidth
                    name={name}
                    label={label}
                />
            </Grid>
        );
        */

    return (
        <Controller
            control={control}
            name={name}
            render={({ field }) => (
                <TextField
                    fullWidth
                    label={label}
                    required
                />
            )}
        />
    )
}

export default FormInput;
